import React, { Component } from 'react';
import './App.css';

import { Icon, Table } from 'semantic-ui-react'
import 'semantic-ui-css/semantic.min.css'

class App extends Component {

  state = {
    restaurants: []
  }

  componentDidMount() {
    this.getRestaurants()
  }

  getRestaurants = _ => {
    fetch('http://localhost:5000/restaurants')
    .then(response => response.json())
    .then(response => this.setState({ restaurants: response.data }))
    .catch(err => console.error(err));
  }

  renderRestaurant = ({ restrntID, Name, Phone, Address, Website, Delivery  }) => 
    <Table.Row key={restrntID}>
      <Table.Cell>{Name}</Table.Cell>
      <Table.Cell>{Phone}</Table.Cell>
      <Table.Cell>{Address}</Table.Cell>
      <Table.Cell>{Website}</Table.Cell>
      <Table.Cell>{Delivery}</Table.Cell>
    </Table.Row>

  render() {
    const { restaurants } = this.state
    return (

      <div className="App">
        <Table celled>
                <Table.Header>
                <Table.Row>
                    <Table.HeaderCell>Name</Table.HeaderCell>
                    <Table.HeaderCell>Phone</Table.HeaderCell>
                    <Table.HeaderCell>Adress</Table.HeaderCell>
                    <Table.HeaderCell>Website</Table.HeaderCell>
                    <Table.HeaderCell>Notes</Table.HeaderCell>
                </Table.Row>
                </Table.Header>

                <Table.Body>
                {restaurants.map(this.renderRestaurant)}
                <Table.Row positive>
                    <Table.Cell>Jimmy</Table.Cell>
                    <Table.Cell></Table.Cell>
                    <Table.Cell>None</Table.Cell>
                </Table.Row>

                </Table.Body>
        </Table>

      </div>
    )
  }
}

export default App;
