import React, { Component } from 'react';
import './App.css';

class App extends Component {

  state = {
    restaurants: []
  }

  componentDidMount() {
    this.getRestaurants()
  }

  getRestaurants = _ => {
    fetch('http://localhost:5000/restaurants')
    .then(response => response.json())
    .then(response => this.setState({ restaurants: response.data }))
    .catch(err => console.error(err));
  }

  renderRestaurant = ({ restrntID, Name, Phone, Address, Website, Delivery  }) => <tr key={restrntID}><tc>{Name}</tc><tc>{Phone}</tc><tc>{Address}</tc><tc>{Website}</tc><tc>{Delivery}</tc></tr>

  render() {
    const { restaurants } = this.state
    return (
      <div className="App">
        <table>
          <tr>
            <th>Name</th>
            <th>Phone</th>
            <th>City</th>
            <th>Website</th>
            <th>Delivery</th>
          </tr>
          <tr>{restaurants.map(this.renderRestaurant)}</tr>
        </table>
      </div>
    )
  }
}

export default App;
